import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { connect } from "./redux/blockchain/blockchainActions";
import { fetchData } from "./redux/data/dataActions";
import { Provider as StyletronProvider, DebugEngine } from "styletron-react";
import { Client as Styletron } from "styletron-engine-atomic";
import { Div, StyleReset, ThemeProvider, Button, Text, Container, Section } from "atomize";
import Counter from "./components/counter"
import Welcome from "./components/welcome";
import Header from "./components/header"
import Description from "./components/description"
import GIF from "./components/gif"


const debug =
  process.env.NODE_ENV === "production" ? void 0 : new DebugEngine();

// Create a client engine instance
const engine = new Styletron();

const theme = {
  colors: {
    black900: "#1d1d1e"
  }
};

function App() {

  // blockchain stuff
  const dispatch = useDispatch();
  const blockchain = useSelector((state) => state.blockchain);
  const data = useSelector((state) => state.data);
  const [feedback, setFeedback] = useState("Maybe it's your lucky day!")
  const [mintingNFT, setMintingNFT] = useState(false)

  const mintNFTS = (_amount) => {
    setMintingNFT(true);
    blockchain.dumbDonuts.methods.mint(blockchain.account, _amount).send({
      from: blockchain.account,
      value: blockchain.web3.utils.toWei((0.01 * _amount).toString(), "ether"),
    })
    .once("error", (err) => {
      console.log(err);
      setFeedback("Error");
      setMintingNFT(false);
    })
    .then((receipt) => {
      setFeedback("Success");
      setMintingNFT(false);
    });
  };

  useEffect(() => {
    if (blockchain.account !== "" && blockchain.dumbDonuts !== null) {
      dispatch(fetchData(blockchain.account));
    }
  }, [blockchain.dumbDonuts, dispatch]);


  return (

    <StyletronProvider value={engine} debug={debug} debugAfterHydration>
      <ThemeProvider theme={theme}>
        <StyleReset />
        <Div>
          {blockchain.account === "" || blockchain.dumbDonuts === null ? (
            <Div 
            d="flex" 
            justify="center" 
            align="center" 
            minH="100vh" 
            w="100vw" 
            flexDir="column"
            >
              <Welcome />
              <Button
                  align="center"
                  top="1rem"
                  m={{ b: "40px" }}
                  shadow="3"
                  hoverShadow="4"
                  rounded="circle"
                  onClick={(e) => {
                    e.preventDefault();
                    dispatch(connect());
                  }}
                >
              Connect
              </Button>
              {blockchain.errorMsg !== "" ? (<Text textAlign="center">{blockchain.errorMsg}</Text>) : null}
            </Div>
          ) : (
            <Div 
            d="flex" 
            justify="center" 
            align="center" 
            minH="100vh" 
            w="100vw" 
            flexDir="column"
            >
              <Header />
              <GIF />
              <Description />
              <Text>{Counter.getCount}</Text>
              <Counter />
              <Button
                  align="center"
                  hoverBg="gray800"
                  bg="black900"
                  shadow="3"
                  hoverShadow="4"
                  rounded="circle"
                  disabled={mintingNFT ? 1 : 0}
                  onClick={(e) => {
                    e.preventDefault();
                    mintNFTS(1);
                  }}
                >
                {mintingNFT ? "Minting" : "Mint"}
                </Button>
            </Div>
          )
          }
        </Div>
      </ThemeProvider>
    </StyletronProvider>
  );
  }

export default App;
