import React, { useEffect, useState, useRef, Component } from "react";
import { useDispatch, useSelector } from "react-redux";
import { connect } from "./redux/blockchain/blockchainActions";
import { fetchData } from "./redux/data/dataActions";
import { create } from "ipfs-http-client";
import { Provider as StyletronProvider, DebugEngine } from "styletron-react";
import { Client as Styletron } from "styletron-engine-atomic";
import { Div, StyleReset, ThemeProvider, Button, Text, Container, Section } from "atomize";
import Home from "./Home"

//const ipfsClient = create("");

// QmWvXqAfjXkNvPXMbcZnZHAtuQGaRDjrHNfP4xxn6qjSA5

const debug =
  process.env.NODE_ENV === "production" ? void 0 : new DebugEngine();

// Create a client engine instance
const engine = new Styletron();

const theme = {
  colors: {
    black900: "#1d1d1e"
  }
};

function App() {

  // blockchain stuff
  const dispatch = useDispatch();
  const blockchain = useSelector((state) => state.blockchain);
  const data = useSelector((state) => state.data);
  const [feedback, setFeedback] = useState("Maybe it's your lucky day!")
  const [mintingNFT, setMintingNFT] = useState(false)

  const mintNFTS = (_amount) => {
    setMintingNFT(true);
    blockchain.dumbDonuts.methods.mint(blockchain, _amount)
  }

  useEffect(() => {
    if (blockchain.account !== "" && blockchain.smartContract !== null) {
      dispatch(fetchData(blockchain.account));
    }
  }, [blockchain.dumbDonuts, dispatch]);

  return (
    <StyletronProvider value={engine} debug={debug} debugAfterHydration>
        <ThemeProvider theme={theme}>
          <StyleReset />
          <Div>
            {blockchain.account === "" || blockchain.dumbDonuts === null ? (
              <Div d="flex" justify="center" align="center" minH="100vh" w="100vw" flexDir="column">
                <Text textSize="display2" textWeight="600">Welcome to Dumb Donuts</Text>
                <Button
                  align="center"
                  top="2rem"
                  shadow="3"
                  hoverShadow="4"
                  rounded="circle"
                  onClick={(e) => {
                    e.preventDefault();
                    dispatch(connect());
                  }}
                >
                Connect
                </Button>
                {blockchain.errorMsg !== "" ? (
                  <Text>{blockchain.errorMsg}</Text>
                ) : null}
          </Div>
      ) : (
        <Div d="flex" justify="center" align="center" minH="100vh" w="100vw" flexDir="column">
          <Text textSize="display2" textWeight="600">Dumb Donuts</Text>
          <Text>Dumb Donuts is a collection of NFTs (unique digital collectibles) based on the works of CryptoPunks and Weird Whales.</Text>
          <Text>10,000 donuts have been generated from a buffet of combinations, each with unique traits.</Text>
          <Text>Treat yourself to a donut today!</Text>
          <Button
                  top="2rem"
                  align="center"
                  shadow="3"
                  hoverShadow="4"
                  rounded="circle"
                  onClick={(e) => {
                    e.preventDefault();
                  }}
                >
                Mint
                </Button>
        </Div>
      )}
    </Div>
        </ThemeProvider>
    </StyletronProvider>
  );

}


export default App;
