import React, { Component } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { connect } from "../redux/blockchain/blockchainActions";
import { Div, StyleReset, ThemeProvider, Button, Text, Container, Section, Icon } from "atomize";

//const dispatch = useDispatch();



class Connect extends React.Component {

    constructor() {
        super();
        this.handlePlus = this.handlePlus.bind(this);
    }

    dispatch() {
        console.log('Dispa', this);
        this.setState({ count: this.state.count - 1 });
    }

    render() { 


        return (
            <Div 
                d="flex" 
                justify="center" 
                align="center" 
                minH="100vh" 
                w="100vw" 
                flexDir="column"
            >
                <Text 
                    textSize="display2" 
                    textWeight="600"
                    textAlign="center"
                    m={{ b: "1.5rem" }}
                >
                Welcome to Dumb Donuts
                </Text>
                <Button 
                    hoverBg="gray800"
                    bg="black900"
                    rounded="circle"
                    shadow="3"
                    hoverShadow="4"
                    onClick={(e) => {
                        e.preventDefault();
                        dispatch(connect());
                    }}
                >
                Connect
                </Button>
            </Div>
            )
    }
}

export default Connect;
