import React, { Component } from 'react';
import { Provider as StyletronProvider, DebugEngine } from "styletron-react";
import { Client as Styletron } from "styletron-engine-atomic";
import { Div, StyleReset, ThemeProvider, Button, Text, Container, Section, Icon } from "atomize";

class Counter extends Component {

    state = {
        count: 0,
    };

    constructor() {
        super();
        this.handleAdd = this.handleAdd.bind(this);
    }

    handleAdd() {
        console.log('Add Clicked', this.state.count)
    }

    render() { 

        let styles = ""

        return (
            <Div d="flex" align="center">

                <Button 
                    h="2.5rem"
                    w="2.5rem"
                    hoverBg="gray800"
                    bg="black900"
                    rounded="circle"
                    m={{ l: "1rem", r: "1rem" }}
                    shadow="2"
                    hoverShadow="4"
                >
                    <Icon name="Minus" size="20px" color="white"/>
                </Button>

                <Text p="1rem">{this.state.count}</Text>

                <Button 
                    h="2.5rem"
                    w="2.5rem"
                    hoverBg="gray800"
                    bg="black900"
                    rounded="circle"
                    m={{ l: "1rem", r: "1rem" }}
                    shadow="2"
                    hoverShadow="4"
                    onClick={this.handleAdd}
                >
                    <Icon name="Plus" size="20px" color="white"/>
                </Button>

            </Div>
        );
    }

    formatCount() {
        const { count } = this.state;
        return count === 0 ? 'Zero' : count;
    }
}


export default Counter;
